# -*- coding: utf-8 -*-"""@author: JulienWuthrich"""from flask import Flask, render_template, requestfrom google_drive_downloader import GoogleDriveDownloader as gddapp = Flask(__name__)@app.route('/')def index():   return render_template('test.html')@app.route("/generic", methods=['POST'])def generic():    url = request.form['nm']    file = url.split("/")[-2]    gdd.download_file_from_google_drive(        file_id=file,        dest_path=f'{file}/{file}.zip',        unzip=True    )    print(url)    return render_template('test.html')if __name__ == '__main__':   app.run(debug=True)